import React from 'react';

 window.onload = function() {

  const guestsInput = document.getElementById('guests');
  const addGuestButton = document.getElementById('add-guest');
  const removeGuestButton = document.getElementById('remove-guest');
  let guestsCount = 0;

  addGuestButton.addEventListener('click', function() {
    if (guestsCount < 10) {
      guestsCount++;
      guestsInput.value = guestsCount;
    }
  });

  removeGuestButton.addEventListener('click', function() {
    if (guestsCount > 0) {
      guestsCount--;
      guestsInput.value = guestsCount;
    }
  });

 }
function Booking() {
  return (
    <div>
      <form method="POST" action="external.php">
        <label htmlFor="name">Name:</label>
        <input type="text" id="name" name="name" />

        <label for="guests">Number of Guests:</label>
        <button type="button" id="add-guest">+</button>
        <input type="text" id="guests" name="guests" value="0" readonly/>

        <button type="button" id="remove-guest">-</button>
        <label htmlFor="date">Date:</label>
        <input type="date" id="date" name="date" />

        <button type="submit">Book now</button>
      </form>
    </div>
  );
}


export default Booking;
