import React from 'react';

import  { useState } from 'react';

import guestList from './guestList.php';

function Booking() {
  const [guests, setGuests] = useState(0);

  const handleAddGuest = () => {
    if (guests < 10) {

      setGuests(guests + 1);

       }
  };

  const handleRemoveGuest = () => {
    if (guests > 0) {
      setGuests(guests - 1);

    }
  };

  const [name, setName] = useState("");
  const [result, setResult] = useState("");

    const handleChange = (e) => {
        setName(e.target.value);
    };

    const handleSumbit = (e) => {
        e.preventDefault();
        const form = $(e.target);
        $.ajax({
            type: "POST",
            url: form.attr("action"),
            data: form.serialize(),
            success(data) {
                setResult(data);
            },
        });
    };

  return (
    <div>
    <div>
      <form method="POST" action="http://localhost:8000/guestList.php"
      onSubmit={(event) => handleSumbit(event)}>
        <label htmlFor="name">Name:</label>
        <input type="text" id="name" name="name" onChange={(event) => handleChange(event)} />

        <label for="guests">Number of Guests:</label>
        <button type="button" id="add-guest" onClick={handleAddGuest}>+</button>
        <input type="text" id="guests" name="guests" value={guests} readonly />
        <button type="button" id="remove-guest" onClick={handleRemoveGuest}>-</button>
        <label htmlFor="date">Date:</label>
        <input type="date" id="date" name="date" />

        <button type="submit">Book now</button>
      </form>
    </div>
    <div>
    <p>{result}</p>
    </div>
    </div>
  );
}



export default Booking;
