import React from 'react';

import  { useState } from 'react';



function Booking() {
  const [guests, setGuests] = useState(0);

  const handleAddGuest = () => {
    if (guests < 10) {
      console.log(guests)
      setGuests(guests + 1);
      e.target.value = guests
       }
  };

  const handleRemoveGuest = () => {
    if (guests > 0) {
      setGuests(guests - 1);

    }
  };

  const handleGuestsChange = (event) => {
    const value = parseInt(event.target.value) || 0;
    if (value >= 0 && value <= 10) {
      setGuests(value);
    }
  }
  return (
    <div>
      <form method="POST" action="external.php">
        <label htmlFor="name">Name:</label>
        <input type="text" id="name" name="name" />

        <label for="guests">Number of Guests:</label>
        <button type="button" id="add-guest" onClick={handleAddGuest}>+</button>
        <input type="text" id="guests" name="guests" value={guests} readonly />
        <button type="button" id="remove-guest" onClick={handleRemoveGuest}>-</button>
        <label htmlFor="date">Date:</label>
        <input type="date" id="date" name="date" />

        <button type="submit">Book now</button>
      </form>
    </div>
  );
}



export default Booking;
